'use strict';

var fs = require('fs');
var path = require('path');
var assert = require('assert');
var stringify = require('../');
var cases = [ 'attr', 'doctype', 'nest' ];

cases.forEach(function (name) {
  var json = fs.readFileSync(path.join(__dirname, name + '.json'), 'utf8');
  var html = fs.readFileSync(path.join(__dirname, name + '.html'), 'utf8');

  json = JSON.parse(json);

  assert(stringify(json).trim() === html.trim());

  console.log(' âœ“ %s', name);
});
